/*! minireset.css v0.0.3 | MIT License | github.com/jgthms/minireset.css */html,body,p,ol,ul,li,dl,dt,dd,blockquote,figure,fieldset,legend,textarea,pre,iframe,hr,h1,h2,h3,h4,h5,h6{margin:0;padding:0}h1,h2,h3,h4,h5,h6{font-size:100%;font-weight:normal}ul{list-style:none}button,input,select,textarea{margin:0}html{box-sizing:border-box}*,*:before,*:after{box-sizing:inherit}img,embed,iframe,object,audio,video{height:auto;max-width:100%}iframe{border:0}table{border-collapse:collapse;border-spacing:0}td,th{padding:0;text-align:left}

body	{background:#000; font-family:monospace, sans-serif; font-size:16px; line-height:24px; color:#666; width:100%; height:100%; padding:50px; position:relative;}
/*body	{background-color:#111; background:url(/data/weirdspace.jpg) no-repeat center center; background-size:cover; background-attachment:fixed;}*/

h1		{font-size:4em; font-weight:normal; color:#1400ff;}
h2		{font-size:2em; font-weight:normal;}

a		{color:#FF9800; text-decoration:none;}
code	{white-space:pre-wrap;}
.def			{padding:20px 0;}
.def span		{color:#9e9e9e; font-weight:bold;}
.latest			{width:75%; padding:40px; background-color:rgba(0,0,0,0.9); margin:0 auto;}
.text, .cast	{display:inline-block; vertical-align:top; width:40%;}
.text			{width:60%; padding-right:60px; color:#ccc;}
.text a			{font-size:1em; padding:0 5px;}
.text span		{color:#666;}
.host			{position:absolute; right:40px; bottom:40px; text-align:right;}
.host div		{font-size:14px;}
.host div a		{color:#888; border-bottom:1px dotted #666;}
