/*! minireset.css v0.0.3 | MIT License | github.com/jgthms/minireset.css */html,body,p,ol,ul,li,dl,dt,dd,blockquote,figure,fieldset,legend,textarea,pre,iframe,hr,h1,h2,h3,h4,h5,h6{margin:0;padding:0}h1,h2,h3,h4,h5,h6{font-size:100%;font-weight:normal}ul{list-style:none}button,input,select,textarea{margin:0}html{box-sizing:border-box}*,*:before,*:after{box-sizing:inherit}img,embed,iframe,object,audio,video{height:auto;max-width:100%}iframe{border:0}table{border-collapse:collapse;border-spacing:0}td,th{padding:0;text-align:left}

body	{background:#262626; font-family:monospace, sans-serif; font-size:12px; line-height:24px; color:#ccc; width:100%; height:100%; padding:30px; position:relative;}
/*body	{background-color:#111; background:url(/data/weirdspace.jpg) no-repeat center center; background-size:cover; background-attachment:fixed;}*/

h1		{font-size:4em; font-weight:normal; color:#1400ff;}
h2		{font-size:2em; font-weight:normal;}

a		{color:#FF9800; text-decoration:none;}
code	{white-space:pre-wrap;}
.def			{padding:30px 0 10px 0;}
.def span		{color:#fff;}
.latest			{width:960px; margin:0 auto; padding-bottom:30px; border-bottom:1px dashed #666;}
li				{margin-left:30px;}
.text, .cast	{display:block; width:960px;}
.text a			{font-size:1em; padding:0 5px;}
.text span		{color:#9ad7d6;}
.text div		{padding:10px 40px 0px 0; display:inline-block; width:280px; vertical-align:middle;}
.text div:last-of-type	{width:680px; font-size:20px; padding-right:0; line-height:26px;}
.text div span	{font-size:16px; line-height:20px; padding:20px; display:block;}
.weird_text a	{color:#eee; font-size:16px; text-transform:uppercase;}
.weird_text a:hover	{background:#464646;}
.host			{position:absolute; right:40px; top:40px; text-align:right;}
.host div		{font-size:14px;}
.host div a		{color:#888; border-bottom:1px dotted #666;}
.cast img		{height:180px;}
.last			{padding-bottom:80px;}

/* Larger than mobile */
@media (min-width: 400px) {
	body			{font-size:16px;}
	.text, .cast	{display:inline-block; vertical-align:top; width:100%;}
	.text			{width:100%; padding-top:15px; color:#ccc;}	
}
